import React, { Component } from "react";
import {
  Text,
  View,
  ScrollView,
  StyleSheet,
  TextInput,
  ImageBackground,
  Dimensions,
  CheckBox,
  Image,
  Modal,
  TouchableOpacity,
} from "react-native";
import { FontAwesome5 } from "@expo/vector-icons";

export default class PadocksFieldView extends Component {
  constructor(props) {
    super(props);

    this.state = {
      modalState: false,
      modalStateDelete: false,
    };
  }
  managetrash = () => {
    console.log("modalstate : " + this.state.modalState);

    if (this.state.modalState === true) {
      this.setState({
        modalState: false,
      });
    } else if (this.state.modalState === false) {
      this.setState({
        modalState: true,
      });
    }
  };

  managetrashdelete = () => {
    console.log("modalstate : " + this.state.modalState);

    this.setState({
      modalState: false,
      modalStateDelete: true,
    });
  };

  render() {
    let screenwidth = Dimensions.get("window").width;
    let screeheight = Dimensions.get("window").height;
    return (
      // <ImageBackground
      //   source={require("../../assets/background.png")}
      //   style={{ height: " 100%", width: "100%" }}
      // >
      <View style={{ flex: 1 }}>
        <View style={{ flex: 1 }}>
          <View
            style={{
              flex: 1,
              backgroundColor: "#0E6655",
              flexDirection: "row",
            }}
          >
            <View
              style={{
                flex: 1,
                // backgroundColor: "green",
                marginLeft: 20,
              }}
            >
              <TouchableOpacity
                onPress={() => {
                  this.props.navigation.openDrawer();
                }}
                style={{ marginTop: 30 }}
              >
                <FontAwesome5
                  name="bars"
                  size={24}
                  color="#000000"
                ></FontAwesome5>
              </TouchableOpacity>
            </View>
            <View
              style={{
                flex: 4,
                // backgroundColor: "purple",
                alignItems: "center",
              }}
            >
              <Text
                style={{
                  fontSize: 22,
                  color: "white",
                  marginTop: 30,

                  fontWeight: "bold",
                }}
              >
                Land & Crop Management
              </Text>
            </View>
            <View
              style={{
                flex: 1,
                // backgroundColor: "pink",
              }}
            ></View>
          </View>
        </View>

        <View style={{ flex: 9 }}>
          <View style={{ flex: 1 }}></View>
          <View style={{ flex: 1 }}>
            {/* category card start------------------------------------------------------------------------------------------------------------------- */}

            <View
              style={{
                backgroundColor: "rgba(52, 52, 52, 0.0)",
                paddingVertical: 18,
                paddingHorizontal: 20,
                borderRadius: 23,
              }}
            >
              <TouchableOpacity
                onPress={() => {
                  this.props.navigation.navigate("CroporFieldEventViewDrawer");
                }}
              >
                <View
                  style={{
                    backgroundColor: "pink",
                    paddingVertical: 30,
                    borderTopColor: "black",
                    paddingHorizontal: 30,
                    borderRadius: 23,
                    width: 340,
                    borderWidth: 1,
                    borderColor: "black",
                  }}
                >
                  <View style={{ flexDirection: "row" }}>
                    <View
                      style={{
                        flex: 10,
                        alignItems: "center",
                        justifyContent: "center",
                      }}
                    >
                      <Text
                        style={{
                          fontWeight: "bold",
                          fontSize: 26,
                          color: "#222222",
                        }}
                      >
                        Add Field/ Paddock
                      </Text>
                    </View>
                  </View>
                </View>
              </TouchableOpacity>
              <View
                style={{
                  position: "absolute",

                  alignItems: "center",

                  right: 10,
                  bottom: 80,
                }}
              >
                <Image
                  source={require("../../assets/animal.webp")}
                  style={{ height: 100, width: 100 }}
                />
              </View>
            </View>

            {/* category card------------------------------------------------------------------------------------------------------------------- */}

            {/* category card start------------------------------------------------------------------------------------------------------------------- */}

            <View
              style={{
                backgroundColor: "rgba(52, 52, 52, 0.0)",
                marginTop: 30,
                paddingVertical: 18,
                paddingHorizontal: 20,
                borderRadius: 23,
              }}
            >
              <TouchableOpacity
                onPress={() => {
                  this.props.navigation.navigate("PurchasesDrawer");
                }}
              >
                <View
                  style={{
                    backgroundColor: "pink",
                    paddingVertical: 30,
                    borderTopColor: "black",
                    paddingHorizontal: 30,
                    borderRadius: 23,
                    width: 340,
                    borderWidth: 1,
                    borderColor: "black",
                  }}
                >
                  <View style={{ flexDirection: "row" }}>
                    <View
                      style={{
                        flex: 10,
                        alignItems: "center",
                        justifyContent: "center",
                      }}
                    >
                      <Text
                        style={{
                          fontWeight: "bold",
                          fontSize: 26,
                          color: "#222222",
                        }}
                      >
                        Add Crop / Field Event
                      </Text>
                    </View>
                  </View>
                </View>
              </TouchableOpacity>
              <View
                style={{
                  position: "absolute",

                  alignItems: "center",

                  right: 10,
                  bottom: 80,
                }}
              >
                <Image
                  source={require("../../assets/animal.webp")}
                  style={{ height: 100, width: 100 }}
                />
              </View>
            </View>

            {/* category card------------------------------------------------------------------------------------------------------------------- */}
          </View>
          <View style={{ flex: 1 }}></View>
        </View>
      </View>
      // </ImageBackground>
    );
  }
}
const styles = StyleSheet.create({
  textInput: {
    height: 50,
    borderRadius: 25,
    borderWidth: 0.5,
    marginHorizontal: 20,
    paddingLeft: 10,
    marginVertical: 5,
    borderColor: "#689F38",
    backgroundColor: "#ffffff",
    marginTop: 15,
  },
});
